css:
    body {
        font-size: 13px;
        font-family: Arial, sans-serif;
    }

    .header-table td {
        padding: 4px 0;
    }

    .divider {
        border: none;
        border-top: 1px solid #000;
        margin: 10px 0;
    }

    .items-table {
        width: 100%;
        border-collapse: collapse;
        margin-top: 10px;
    }

    .items-table th,
    .items-table td {
        border-bottom: 1px solid #000;
        padding: 6px 4px;
    }

    .items-table th {
        text-align: left;
        font-weight: bold;
    }

    .right {
        text-align: right;
    }

    .total-table {
        width: 100%;
        margin-top: 10px;
    }

    .total-value {
        width: 140px;
        font-weight: bold;
        text-align: right;
    }

    .amount-words {
        margin-top: 12px;
        font-size: 13px;
    }

    .signature {
        margin-top: 40px;
        text-align: right;
    }
    
/* Keep footer with totals */
  .thanks-footer {
    page-break-before: avoid;
    break-before: avoid;
  }
  
.thanks-footer {
  position: relative; /* Changed from fixed */
  bottom: auto; /* Reset bottom positioning */
  left: auto; /* Reset left positioning */
  width: 100%;
  text-align: center;
  /*font-weight: 600;*/
  font-size: 14px;
  padding: 8px 0;
  margin-top: 40px; /* Add spacing from content above */
  background: #fff;
  /*color: #ac8d62;*/
}

@media print {
  body { 
    margin-bottom: 50px !important; 
  }
  .thanks-footer {
    position: fixed;
    bottom: 0;
    width: 100%;
    left: 0;
  }
    /* Repeat table header on every page */
  thead {
    display: table-header-group;
  }

  /* Prevent row splitting */
  .items-table tr {
    page-break-inside: avoid;
    break-inside: avoid;
  }

  /* 🔥 FORCE PAGE BREAK AFTER EVERY 20 ITEM ROWS */
  .items-table tbody tr:nth-child(22n) {
    page-break-after: always;
    break-after: page;
  }
}
